
function is_touch_device() {              //is_touch_device?
  return (('ontouchstart' in window) || (navigator.MaxTouchPoints > 0) || (navigator.msMaxTouchPoints > 0));
}

let myself = document.getElementById("myselfs");
let ftr = document.getElementById("ftr");
let myInfo = document.getElementById("myInfo");
let skils = document.getElementById("skils");
let myname = document.getElementById("myname");
let check = true;


if (!is_touch_device()) {             //no-tach device
  
  new ClipboardJS('.btn');
  var clipboard = new ClipboardJS('.btn');                    // saved to click 
  let tooltipText;
  clipboard.on('success', function (e) {
    tooltipText = e.trigger.attributes.tooltip.value;
    e.trigger.attributes.tooltip.value = "Copied!";

    e.trigger.onmouseover = function () {
      e.trigger.attributes.tooltip.value = tooltipText;
    };
    e.clearSelection();
  });

  myself.onclick = function () {
    if (check) {
      animate({
        duration: 1000,
        timing: function (timeFraction) {
          return Math.pow(timeFraction, 10);
        },
        draw: function (progress) {
          ftr.style.right = progress * 290 + 'px';
          ftr.style.top = progress * 0 + 'px';
          ftr.classList.add("animfooter");

          skils.style.zIndex = 20;
          skils.style.transition = "all 1s ease";
          skils.style.transitionDelay = ".5s";
          skils.style.opacity = progress * 1;

          myInfo.style.zIndex = 9;
          myInfo.style.transition = "all 1s ease";
          myInfo.style.transitionDelay = ".5s";
          myInfo.style.opacity = progress * 0.9;

          myname.classList.add("gradientactive");

          myself.classList.add("about");
        }
      });
      check = !check;
    }
    else {      
      animate({
        duration: 1000,
        timing: function (timeFraction) {
          return Math.pow(timeFraction, 10);
        },
        draw: function (progress) {
          ftr.style.right = progress * 0 + 'px';
          ftr.style.top = progress * 100 + 'px';

          skils.style.transition = "all .5s ease";
          skils.style.transitionDelay = "1s";
          skils.style.opacity = progress * 0;
          skils.style.zIndex = -20;

          myInfo.style.transition = "all .2s ease";
          myInfo.style.transitionDelay = ".01s";
          myInfo.style.opacity = progress * 0;
          myInfo.style.zIndex = -9;

          myname.classList.remove("gradientactive");
          myself.classList.remove("about");
        }
      });
      check = !check;
    }
  };

} else {                                                    //tach device

  let gm = document.getElementById("gm");
  gm.removeAttribute('tooltip');
  gm.setAttribute('href', 'mailto:kovallarm@gmail.com');

  let mob = document.getElementById("mob");
  mob.removeAttribute('tooltip');
  mob.setAttribute('href', 'tel:+380989752993');


  myself.classList.remove("myself");
  myself.classList.remove("about");
  myself.classList.add("myselfTach");

  myself.onclick = function () {

    if (check) {
      animate({
        duration: 1000,
        timing: function (timeFraction) {
          return Math.pow(timeFraction, 10);
        },
        draw: function (progress) {
          ftr.style.top = progress * 0 + 'px';
          ftr.classList.add("animfooter");

          skils.style.bottom = "0px";
          skils.style.zIndex = 20;
          skils.style.transition = "all 1s ease";
          skils.style.transitionDelay = ".5s";
          skils.style.opacity = progress * 1;

          myInfo.style.top = "0px";
          myInfo.style.left = "0px";
          myInfo.style.zIndex = 9;
          myInfo.style.transition = "all 1s ease";
          myInfo.style.transitionDelay = ".5s";
          myInfo.style.opacity = progress * 0.9;
          
          myname.classList.add("gradientactive");

          myself.classList.remove("myselfTach");
          myself.classList.add("aboutTach");
        }
      });
      check = !check;
    }
    else {
      animate({
        duration: 1000,
        timing: function (timeFraction) {
          return Math.pow(timeFraction, 10);
        },
        draw: function (progress) {
          ftr.style.right = progress * 0 + 'px';
          ftr.style.top = progress * 100 + 'px';

          skils.style.transition = "all .5s ease";
          skils.style.transitionDelay = "1s";
          skils.style.opacity = progress * 0;
          skils.style.zIndex = -20;

          myInfo.style.transition = "all .2s ease";
          myInfo.style.transitionDelay = ".01s";
          myInfo.style.opacity = progress * 0;
          myInfo.style.zIndex = -9;

          myname.classList.remove("gradientactive");


          myself.classList.add("myselfTach");
          myself.classList.remove("aboutTach");

        }
      });
      check = !check;
    }
  };
}